#include <cstdio>int max (int a, int b){    return a > b ? a : b;}int minof (int x[], int l){    int min = x[0];    for (int i = 0; i < l; i ++)    {        if (x[i] < min)        {            min = x[i];        }    }    return min;}int main (){    int n, shields[2][128], shieldsum[2][128];    int i, j;    scanf ("%d", &n);    for (i = 0; i < 2; i ++)    {        for (j = 0; j < n; j ++)        {            scanf ("%d", &shields[i][j]);        }    }    for (i = 0; i < n; i ++)    {        shieldsum[0][i] = shields[0][i] + shields[1][i];    }    int r1 = 0, r2 = n - 1, temp;    while (r1 < r2)    {        temp = shields[1][r1];        shields[1][r1] = shields[1][r2];        shields[1][r2] = temp;        r1 ++, r2 --;    }    for (i = 0; i < n; i ++)    {        shieldsum[1][i] = shields[0][i] + shields[1][i];    }    printf ("%d\n", max (minof (shieldsum[0], n), minof (shieldsum[1], n)));return 0;}